import{_ as e,k as r}from"./index.acaf92bd.js";const t={data(){return{}},methods:{}},p=r('<h3>\u8FD9\u91CC\u9605\u8BFB\u7684\u662FIdentifying Privilege Separation Vulnerabilities in IoT Firmware with Symbolic Execution\u3002\u672C\u6587\u4E3B\u8981\u9488\u5BF9\u7684\u662F\u56FA\u4EF6\u4E2D\u7684\u7279\u6743\u5206\u79BB\u6A21\u578B\u5185\u7684\u6F0F\u6D1E\u3002\u6587\u4E2D\u63D0\u51FA\u4E86\u4E00\u79CD\u57FA\u4E8EAngr\u6846\u67B6\u7684\uFF0C\u5BF9\u56FA\u4EF6\u5206\u6790\u8FDB\u884C\u4E86\u7279\u522B\u4F18\u5316\u7684\u6539\u8FDB\u7279\u6743\u5206\u79BB\u6F0F\u6D1E\u7684\u65B9\u6CD5\u3002</h3><p><b>\u7279\u6743\u5206\u79BB</b>\u662F\u4E00\u79CDLinux\u548COpenSSL\u534F\u8BAE\u91CC\u5E38\u7528\u7684\u5B89\u5168\u7B56\u7565\u3002\u7B80\u5355\u6765\u8BF4\uFF0C\u5C31\u662F\u901A\u8FC7\u5C06\u672C\u5C5E\u4E8E\u4E00\u4E2A\u7528\u6237\u7684\u7279\u6743\u5206\u5272\u7ED9\u4E0D\u540C\u7684\u7A0B\u5E8F\u548C\u7528\u6237\uFF0C\u4EE5\u6B64\u4FDD\u8BC1\u5355\u4E2A\u7A0B\u5E8F\u6216\u7528\u6237\u7684\u6F0F\u6D1E\u9020\u6210\u7684\u5F71\u54CD\u6700\u5C0F\u3002\u4F8B\u5982\uFF0C\u5BF9\u4E8E\u4E00\u4E2A\u6587\u672C\u7F16\u8F91\u7A0B\u5E8F\uFF0C\u6211\u4EEC\u53EF\u4EE5\u628A\u5B83\u5206\u6210\u9605\u8BFB\u5668/\u7F16\u8F91\u5668\u4E24\u4E2A\u90E8\u5206\uFF0C\u5206\u522B\u7531\u4E0D\u540C\u7684\uFF08\u5B50\uFF09\u7528\u6237\u542F\u52A8\uFF0C\u5176\u4E2D\u9605\u8BFB\u5668A\u53EA\u5BF9\u6587\u4EF6\u62E5\u6709\u8BFB\u53D6\u6743\u9650\uFF0C\u7F16\u8F91\u5668B\u53EA\u62E5\u6709\u5199\u5165\u6743\u9650\uFF0C\u8FD9\u6837\uFF0C\u5982\u679CA\u6709\u6F0F\u6D1E\uFF0C\u653B\u51FB\u8005\u65E0\u6CD5\u66F4\u6539\u7CFB\u7EDF\u4E2D\u7684\u4FE1\u606F\uFF0CB\u6709\u6F0F\u6D1E\uFF0C\u653B\u51FB\u8005\u65E0\u6CD5\u67E5\u770B\u7CFB\u7EDF\u4E2D\u7684\u4FE1\u606F\uFF0C\u8FD9\u6837\u5C31\u8FDB\u4E00\u6B65\u63D0\u9AD8\u4E86\u7CFB\u7EDF\u7684\u5B89\u5168\u6027\u3002</p><p>\u5148\u6765\u770B\u4E00\u4E0B\u8BBA\u6587\u4E3A\u4E86\u7814\u7A76\u800C\u505A\u7684\u51C6\u5907\u5DE5\u4F5C\u4E00\u3002\u8BBA\u6587\u6536\u96C6\u4E86\u7269\u7406\u64CD\u4F5C\u3001APP\u9065\u63A7\u3001\u4E91\u7AEF\u9065\u63A7\u7B49\u56FA\u4EF6\u64CD\u4F5C\u65B9\u5F0F\u4E0B\uFF0C\u56FA\u4EF6\u6240\u6267\u884C\u7684\u4E8C\u8FDB\u5236\u4EE3\u7801\uFF0C\u7136\u540E\u53D1\u73B0\u8FD9\u4E9B\u65B9\u5F0F\u7684\u4EE3\u7801\u4E2D\u5B58\u5728\u4EA4\u96C6\uFF0C\u6BD4\u5982\u5BF9\u4E8E\u65B9\u6CD5A\u6765\u8BF4\uFF0C\u8DEF\u5F84\u662Fa-b-c-d,\u800C\u65B9\u6CD5B\u662Fe-f-c-g\uFF0C\u8FD9\u6837A\u548CB\u90FD\u6709\u540C\u6837\u7684\u6B65\u9AA4c\uFF0C\u5C31\u5B58\u5728\u6267\u884CA\u7684\u65F6\u5019\uFF0C\u8DF3\u8F6C\u5230B\u7684\u53EF\u80FD\u6027\uFF0C\u56E0\u6B64\u8BA4\u4E3A\u53EF\u80FD\u5B58\u5728\u6F5C\u5728\u8DE8\u65B9\u6CD5\u7684\u6267\u884C\u65B9\u5F0F\uFF0C\u4E5F\u5C31\u662F\u7ED5\u8FC7\u7279\u6743\u5206\u79BB\uFF0C\u8D8A\u6743\u6267\u884C\u65B9\u6CD5\u7684\u5371\u9669\u6027\u3002</p><p>\u800C\u56FA\u4EF6\u6F0F\u6D1E\uFF0C\u5728\u9690\u79C1\u7F3A\u9677\uFF08privacy leak\uFF09\uFF0C\u7279\u6743\u65C1\u9053\uFF08authentication bypass\uFF09\uFF0C\u5185\u5B58\u6C61\u70B9\uFF08memory corruption flaws\uFF09\u7B49\u65B9\u9762\u5DF2\u7ECF\u6709\u4E86\u5F88\u591A\u3002\u540C\u65F6\uFF0C\u8BBA\u6587\u63D0\u51FA\uFF0C\u4F20\u7EDF\u7684\u9759\u6001\u548C\u52A8\u6001\u65B9\u6CD5\u5BF9\u4E8E\u56FA\u4EF6\u6F0F\u6D1E\u6316\u6398\uFF0C\u5177\u6709\u5982\u4E0B\u51E0\u4E2A\u4E25\u91CD\u6311\u6218\uFF1A\u2460\u5BF9\u4E8E\u8F7B\u91CF\u7EA7\u7684\u56FA\u4EF6\u6620\u50CF\uFF0C\u7531\u4E8E\u6267\u884C\u548C\u8FDE\u63A5\u683C\u5F0F\u672A\u77E5\uFF0C\u5F53\u524D\u7684\u4E8C\u8FDB\u5236\u5206\u6790\u5DE5\u5177\u96BE\u4EE5\u5224\u65AD\u90A3\u4E9B\u4FE1\u606F\u662F\u5FC5\u8981\u7684\uFF1B\u2461\u52A8\u6001\u65B9\u6CD5\u5F80\u5F80\u9700\u8981\u57FA\u4E8ELinux\u73AF\u5883\u6216\u662F\u5B9E\u673A\u73AF\u5883\uFF0C\u4F7F\u5F97\u5176\u6CDB\u7528\u6027\u8F83\u4F4E\uFF1B\u2462\u5373\u4F7F\u7B26\u53F7\u6267\u884C\u53EA\u9009\u62E9\u4E00\u6761\u8DEF\u5F84\uFF0C\u4E5F\u53EF\u80FD\u4F1A\u7531\u4E8E\u65E0\u9650\u5FAA\u73AF\u6216\u8005\u590D\u6742\u8BA1\u7B97\u5BFC\u81F4\u7684\u8DEF\u5F84\u7206\u70B8\u3002</p><p>\u672C\u6587\u7684\u8D21\u732E\u6709\u5982\u4E0B\u56DB\u6761\uFF1A\u2460\u9996\u6B21\u6DF1\u5165\u5206\u6790\u4E86\u56FA\u4EF6\u4E2D\u7684\u7279\u6743\u5206\u79BB\u6F0F\u6D1E\uFF1B\u2461\u57FA\u4E8EAngr\u6846\u67B6\u505A\u51FA\u4E86\u62D3\u5C55\uFF0C\u5305\u62EC\u52A0\u8F7D\u4FE1\u606F\u63D0\u53D6\u3001\u5E93\u529F\u80FD\u8BC6\u522B\u548C\u95F4\u63A5\u63A7\u5236\u6D41\u6062\u590D\uFF1B\u2462\u5728\u7B26\u53F7\u6267\u884C\u7684\u9876\u5C42\u8BBE\u8BA1\u4E86\u4E00\u79CD\u65B0\u7684\u8DEF\u5F84\u63A2\u7D22\u6846\u67B6\uFF0C\u7F13\u89E3\u4E86\u8DEF\u5F84\u7206\u70B8\u7684\u95EE\u9898\uFF1B\u2463\u6210\u529F\u627E\u5230\u4E86\u4E00\u4E9B\u7269\u8054\u7F51\u786C\u4EF6\u8BBE\u5907\u7684\u7279\u6743\u5206\u79BB\u6F0F\u6D1E\u3002</p><h3>Gerbil\u6846\u67B6\u7684\u8BBE\u8BA1\u548C\u5B9E\u73B0</h3><p>Gerbil\u6846\u67B6\u662F\u57FA\u4E8EAngr\u8FED\u4EE3\u5F00\u53D1\u7684\u3002Angr\u662F\u4E00\u79CD\u7528\u4E8E\u4E8C\u8FDB\u5236\u6587\u4EF6\u7684\u9759\u6001\u548C\u52A8\u6001\u7B26\u53F7\u5206\u6790\u6F0F\u6D1E\u6316\u6398\u6846\u67B6\uFF0C\u800CGerbil\u66F4\u4E13\u6CE8\u4E8E\u5728\u524D\u7AEF\u6A21\u7EC4\u4E2D\u63D0\u53D6\u52A0\u8F7D\u4FE1\u606F\u548C\u6062\u590D\u56FA\u4EF6\u7684\u5E93\u51FD\u6570\uFF0C\u5E76\u5728\u540E\u7AEF\u7B26\u53F7\u6267\u884C\u5F15\u64CE\u7684\u9876\u5C42\u589E\u52A0\u4E86\u8DEF\u5F84\u63A2\u7D22\u6846\u67B6\uFF0C\u540C\u65F6\u8FD8\u989D\u5916\u63D0\u4F9B\u4E86\u4E86\u7528\u6237\u624B\u52A8\u5206\u7247\u7684\u65B9\u5F0F\u6765\u63D0\u9AD8\u547D\u4E2D\u7387\u7684\u65B9\u5F0F\u3002</p><p>\u9996\u5148\u662F<b>\u52A0\u8F7D\u4FE1\u606F\u63D0\u53D6</b>\uFF0C\u4E00\u4E9B\u8F83\u5927\u7684\u6210\u719F\u56FA\u4EF6\u7CFB\u7EDF\u4F1A\u6709\u8BE6\u7EC6\u7684\u542F\u52A8\u4FE1\u606F\uFF0C\u4F46\u662F\u5BF9\u4E8E\u4E00\u4E9B\u5B9A\u5411\u5F00\u53D1\u7684\u5C0F\u578B\u56FA\u4EF6\u8BBE\u5907\uFF0C\u5E76\u6CA1\u6709\u6807\u51C6\u5316\u7684\u542F\u52A8\u4FE1\u606F\u3002\u4F46\u540C\u79CD\u57FA\u677F\u7684\u56FA\u4EF6\u4F1A\u6709\u76F8\u540C\u7684\u542F\u52A8\u4FE1\u606F\u7ED3\u6784\uFF0C\u56E0\u6B64\u8BBA\u6587\u5BF9\u4E8E\u8FD9\u4E00\u90E8\u5206\u91C7\u53D6\u4E86\u4EBA\u5DE5\u7EF4\u62A4\u5E93\u7684\u65B9\u5F0F\uFF0C\u6765\u5B9E\u73B0\u5BF9\u4E8E\u4E0D\u540C\u7CFB\u7EDF\u4E0B\u7684\u52A0\u8F7D\u4FE1\u606F\u63D0\u53D6\u3002\u901A\u8FC7\u52A0\u8F7D\u4FE1\u606F\uFF0C\u53EF\u4EE5\u8BC6\u522B\u7A0B\u5E8F\u5206\u7C7B\uFF0C\u4ECE\u800C\u8FDB\u4E00\u6B65\u786E\u8BA4\u5C06\u8981\u4F7F\u7528\u7684\u7B26\u53F7\u6267\u884C\u7B56\u7565\u3002</p><p>\u7136\u540E\u662F<b>\u5E93\u65B9\u6CD5\u8BC6\u522B</b>\u3002\u5E93\u65B9\u6CD5\u5C31\u662F\u5728\u56FA\u4EF6\u6700\u5E95\u5C42\u7684\u6838\u5FC3\u65B9\u6CD5\uFF0C\u8BC6\u522B\u51FA\u6838\u5FC3\u65B9\u6CD5\uFF0C\u6709\u52A9\u4E8E\u6700\u5C0F\u5316\u56FA\u4EF6\u5206\u6790\u6240\u9700\u8FDB\u884C\u7684\u5DE5\u4F5C\uFF0C\u5C24\u5176\u662F\u5B89\u5168\u65B9\u9762\u7684\u5DE5\u4F5C\u3002\u5728\u8FD9\u91CC\uFF0C\u4F7F\u7528\u4E86FLIRT\u6765\u8FDB\u884C\u5206\u6790\uFF0C\u4E3A\u4E86\u66F4\u9AD8\u7684\u6548\u7387\uFF0C\u4F5C\u8005\u6536\u96C6\u4E86\u4E00\u4E9B\u516C\u5F00\u7684\u5E93\u51FD\u6570\uFF0C\u7136\u540E\u5BF9\u4ED6\u4EEC\u8FDB\u884C\u7F16\u8BD1\u5206\u6790\uFF0C\u4ECE\u800C\u83B7\u5F97\u4E86\u4E00\u4E2A\u7528\u4E8E\u6A21\u5F0F\u8BC6\u522B\u7684\u5E93\uFF0C\u4ECE\u800C\u964D\u4F4E\u6062\u590D\u5E93\u65B9\u6CD5\u7684\u96BE\u5EA6\u3002</p><p>\u518D\u540E\u9762\u662F<b>\u8DEF\u5F84\u63A2\u7D22\u6A21\u5F0F</b>\uFF0C\u5728Angr\u5BF9\u7269\u8054\u7F51\u56FA\u4EF6\u8FDB\u884C\u6D4B\u8BD5\u7684\u65F6\u5019\uFF0C\u5BB9\u6613\u9020\u6210\u975E\u9884\u671F\u7684\u95F4\u63A5\u5524\u9192\u548C\u8DEF\u5F84\u7206\u70B8\uFF0C\u56E0\u6B64\u6587\u4E2D\u91C7\u7528\u4E86\u4E0B\u9762\u4E09\u4E2A\u65B9\u6CD5\u6539\u8FDB\u8FD9\u4E00\u95EE\u9898\u3002\u7B2C\u4E00\u4E2A\u662F<b style="color:blueviolet;">\u5F15\u5165\u9AD8\u7EA7\u522B\u7EA6\u675F</b>,\u8FD9\u91CC\u4F7F\u7528\u4E86\u521A\u521A\u83B7\u5F97\u7684\u5E93\u51FD\u6570\uFF0C\u5F53\u6267\u884C\u8DEF\u5F84\u8D70\u5230\u5E93\u51FD\u6570\u65F6\uFF0C\u5C31\u5728\u6A21\u62DF\u72B6\u6001\u4E2D\u8BB0\u5F55\u4E0B\u6765\u5E93\u51FD\u6570\u540D\u548C\u5F53\u524D\u5BC4\u5B58\u5668\u72B6\u6001\u3002\u901A\u8FC7\u5BF9\u8FD9\u4E9B\u65B9\u6CD5\u7684\u8BB0\u5F55\uFF0C\u53EF\u4EE5\u8BC6\u522B\u51FA\u6765\u65E0\u9650\u5FAA\u73AF\u7B49\u5F02\u5E38\u72B6\u6001\u3002\u7B2C\u4E8C\u4E2A\u662F<b style="color:blueviolet;">\u8DF3\u8FC7\u9009\u62E9\u7684\u5E93\u65B9\u6CD5</b>\uFF0C\u7531\u4E8E\u5E93\u51FD\u6570\u5BF9\u4E8E\u7A0B\u5E8F\u6267\u884C\u65E5\u5FD7\u5F80\u5F80\u5173\u7CFB\u4E0D\u5927\uFF0C\u56E0\u6B64\u8FD9\u91CC\u91C7\u7528\u4E86\u5982\u4E0B\u65B9\u5F0F\uFF0C\u5F53\u7A0B\u5E8F\u6267\u884C\u5230\u590D\u6742\u5E93\u51FD\u6570\u65F6\uFF0C\u76F4\u63A5\u8DF3\u8FC7\u5E93\u51FD\u6570\uFF0C\u5206\u914D\u4E00\u4E2A\u65B0\u7684\u7B26\u53F7\u4F5C\u4E3A\u8FD9\u4E2A\u5E93\u51FD\u6570\u7684\u8F93\u51FA\uFF0C\u7136\u540E\u7EE7\u7EED\u6267\u884C\u540E\u9762\u7684\u90E8\u5206\u3002\u7B2C\u4E09\u4E2A\u662F<b style="color:blueviolet;">\u95F4\u63A5\u8C03\u7528\u6062\u590D</b>\uFF0C\u5728\u5B9E\u9A8C\u4E2D\uFF0C\u4F5C\u8005\u53D1\u73B0\u4E86\u4E24\u79CDAngr\u65E0\u6CD5\u8BC6\u522B\uFF0C\u4F46\u662F\u56FA\u4EF6\u8BBE\u5907\u7ECF\u5E38\u4F7F\u7528\u7684\u95F4\u63A5\u8C03\u7528\u65B9\u5F0F\uFF0C\u4E00\u79CD\u662F\u56DE\u8C03\u51FD\u6570\uFF08A\u5524\u9192B\u6765\u63D0\u4F9B\u4E00\u4E2A\u4FE1\u606F\uFF0C\u5F53B\u83B7\u5F97\u8FD9\u4E2A\u4FE1\u606F\u4E4B\u540E\uFF0C\u5C06\u53C2\u6570\u4F20\u9012\u7ED9A\u5E76\u4E14\u6267\u884C\uFF09\uFF0C\u53E6\u4E00\u79CD\u662F\u4FE1\u606F\u961F\u5217\u8C03\u7528\u65B9\u5F0F\u3002\u6587\u4E2D\u901A\u8FC7\u63D0\u524D\u5BF9\u95F4\u63A5\u8C03\u7528\u5173\u7CFB\u8FDB\u884C\u5206\u6790\u7684\u65B9\u5F0F\uFF0C\u4ECE\u800C\u5B9E\u73B0\u5728\u8FD9\u4E24\u79CD\u95F4\u63A5\u8C03\u7528\u51FA\u73B0\u65F6\u7684\u5373\u65F6\u8BC6\u522B\u3002</p><p>\u7528\u6237\u5206\u7247\u6BD4\u8F83\u7B80\u5355\uFF0C\u8FD9\u91CC\u6309\u4E0B\u4E0D\u8868\u3002</p><p>\u5177\u4F53\u7684\u5B9E\u9A8C\u6570\u636E\u4E5F\u8BF7\u5728\u8BBA\u6587\u4E2D\u67E5\u770B\u3002</p>',12);function i(o,n,a,b,c,l){return p}const u=e(t,[["render",i]]);export{u as default};
